  PRIMARY KEY (`id`),
  KEY `instructions_category_id_fk` (`id_category`),
  KEY `instructions_users_id_fk` (`id_user`),
  CONSTRAINT `instructions_category_id_fk` FOREIGN KEY (`id_category`) REFERENCES `categories` (`id`),
